$("document").ready(function(){var a=($(window).height()-200-$("#page-content").height())/2;$("#page-content").css({"padding-top":a});$("#pwd").blur(function(){if($("#pwd").val().length<6)$("#pwd-error").css({opacity:1});else $("#pwd-error").css({opacity:0})});$("#confirm-pwd").blur(function(){var a=$("#confirm-pwd").val();if(a!=$("#pwd").val())$("#confirm-pwd-error").css({opacity:1});else $("#confirm-pwd-error").css({opacity:0})});$("#reset-pwd").submit(function(){if($("#confirm-pwd").val()==$("#pwd").val()&&$("#pwd").val()!=""&&$("#pwd").val().length>=6)return true;else if($("#confirm-pwd").val()!=$("#pwd").val()&&$("#pwd").val()!=""){$("#confirm-pwd-error").css({opacity:1})}return false})})