$(document).ready(function(){var a=($(window).height()-250-$("#page-content").height())/2;$("#page-content").css({"padding-top":a});$("#contact-form").submit(function(){$.post("contact-support.php",{type:$("#type").val(),name:$("#form-name").val(),email:$("#form-email").val(),message:$("#form-textarea").val()},function(a){var b=JSON.parse(a);if(b.status==0){$("#contact-form").fadeOut(700,function(){$("#contact-container").replaceWith(b.message)})}});return false})})